import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../utils/axios';
import './CreatePost.css';

const CreatePost = () => {
    const navigate = useNavigate();
    const fileInputRef = useRef(null);
    
    // State aligned with Post model
    const [formData, setFormData] = useState({
        title: '',
        content: '',
        category: 'general',
        tags: [],
        status: 'published'
    });

    const [validationErrors, setValidationErrors] = useState({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    // Categories from Post model enum
    const categories = [
        { value: 'general', label: 'ðŸ“„ General' },
        { value: 'technology', label: 'ðŸ’» Technology' },
        { value: 'lifestyle', label: 'ðŸŒŸ Lifestyle' },
        { value: 'travel', label: 'âœˆï¸ Travel' },
        { value: 'food', label: 'ðŸ½ï¸ Food' },
        { value: 'other', label: 'ðŸ“Œ Other' }
    ];

    const validateForm = () => {
        const errors = {};
        
        // Title validation
        if (!formData.title.trim()) {
            errors.title = 'Title is required';
        }

        // Content validation
        if (!formData.content.trim()) {
            errors.content = 'Content is required';
        }

        setValidationErrors(errors);
        return Object.keys(errors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!validateForm()) {
            return;
        }

        setLoading(true);
        setError('');

        try {
            const token = localStorage.getItem('token');
            if (!token) {
                throw new Error('Please login to create a post');
            }

            const response = await api.post('/blog', 
                {
                    ...formData,
                    title: formData.title.trim(),
                    content: formData.content.trim(),
                    tags: formData.tags.filter(tag => tag.trim()) // Remove empty tags
                },
                {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                }
            );

            if (response.data.success) {
                navigate(`/post/${response.data.post._id}`);
            }
        } catch (err) {
            console.error('Create post error:', err);
            setError(err.response?.data?.message || 'Failed to create post');
        } finally {
            setLoading(false);
        }
    };

    const handleTagInput = (e) => {
        if (e.key === 'Enter' && e.target.value.trim()) {
            e.preventDefault();
            const newTag = e.target.value.trim().toLowerCase();
            if (!formData.tags.includes(newTag)) {
                setFormData({
                    ...formData,
                    tags: [...formData.tags, newTag]
                });
            }
            e.target.value = '';
        }
    };

    const removeTag = (tagToRemove) => {
        setFormData({
            ...formData,
            tags: formData.tags.filter(tag => tag !== tagToRemove)
        });
    };

    return (
        <div className="create-post-container">
            <div className="create-post-card">
                <h1>Create New Post</h1>
                {error && <div className="error-message">{error}</div>}
                
                <form onSubmit={handleSubmit} noValidate>
                    <div className="form-group">
                        <label htmlFor="title">
                            Title <span className="required">*</span>
                        </label>
                        <input
                            type="text"
                            id="title"
                            value={formData.title}
                            onChange={(e) => setFormData({...formData, title: e.target.value})}
                            className={`form-input ${validationErrors.title ? 'invalid' : ''}`}
                            placeholder="Enter post title"
                        />
                        {validationErrors.title && (
                            <div className="error-text">{validationErrors.title}</div>
                        )}
                    </div>

                    <div className="form-group">
                        <label htmlFor="category">Category</label>
                        <select
                            id="category"
                            value={formData.category}
                            onChange={(e) => setFormData({...formData, category: e.target.value})}
                            className="form-select"
                        >
                            {categories.map(cat => (
                                <option key={cat.value} value={cat.value}>
                                    {cat.label}
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className="form-group">
                        <label>Tags</label>
                        <div className="tags-container">
                            {formData.tags.map((tag, index) => (
                                <span key={index} className="tag">
                                    {tag}
                                    <button 
                                        type="button" 
                                        onClick={() => removeTag(tag)}
                                        className="remove-tag"
                                    >
                                        Ã—
                                    </button>
                                </span>
                            ))}
                            <input
                                type="text"
                                placeholder="Add tags (press Enter)"
                                onKeyPress={handleTagInput}
                                className="tag-input"
                            />
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="content">
                            Content <span className="required">*</span>
                        </label>
                        <textarea
                            id="content"
                            value={formData.content}
                            onChange={(e) => setFormData({...formData, content: e.target.value})}
                            className={`form-textarea ${validationErrors.content ? 'invalid' : ''}`}
                            placeholder="Write your post content here..."
                            rows="12"
                        />
                        {validationErrors.content && (
                            <div className="error-text">{validationErrors.content}</div>
                        )}
                    </div>

                    <div className="form-actions">
                        <button 
                            type="button" 
                            className="cancel-button"
                            onClick={() => navigate(-1)}
                        >
                            Cancel
                        </button>
                        <button 
                            type="submit" 
                            className="submit-button" 
                            disabled={loading}
                        >
                            {loading ? (
                                <>
                                    <span className="loading-spinner"></span>
                                    Creating Post...
                                </>
                            ) : (
                                'Create Post'
                            )}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default CreatePost;
