import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './BlogList.css';

const BlogList = () => {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [category, setCategory] = useState('all');

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const response = await axios.get(category === 'all' ? 'http://localhost:5000/api/blog' : `http://localhost:5000/api/blog/category/${category}`);
                setPosts(response.data.posts);
            } catch (err) {
                setError('Failed to fetch posts');
            } finally {
                setLoading(false);
            }
        };

        fetchPosts();
    }, [category]);

    if (loading) return <div className="loading-spinner">Loading posts...</div>;
    if (error) return <div className="error-message">{error}</div>;

    return (
        <div className="blog-list-container">
            <div className="blog-header">
                <h1>Explore Articles</h1>
                <div className="blog-filters">
                    <select 
                        value={category} 
                        onChange={(e) => setCategory(e.target.value)}
                        className="category-filter"
                    >
                        <option value="all">All Categories</option>
                        <option value="technology">Technology</option>
                        <option value="lifestyle">Lifestyle</option>
                        <option value="travel">Travel</option>
                        <option value="food">Food</option>
                    </select>
                    <Link to="/create-post" className="create-post-btn">
                        Write Article
                    </Link>
                </div>
            </div>

            <div className="posts-grid">
                {posts.map(post => (
                    <article key={post._id} className="post-card">
                        <div className="post-image">
                            <img 
                                src={post.imageUrl || '/images/default-blog.jpg'} 
                                alt={post.title}
                                onError={(e) => {
                                    e.target.src = '/images/default-blog.jpg';
                                }}
                            />
                            <span className="category-tag">{post.category}</span>
                        </div>
                        <div className="post-content">
                            <h2>{post.title}</h2>
                            <p>{post.content.substring(0, 150)}...</p>
                            <div className="post-meta">
                                <div className="author-info">
                                    <img 
                                        src={post.author?.avatar || '/images/default-avatar.jpg'} 
                                        alt={post.author?.username}
                                        className="author-avatar"
                                    />
                                    <span>{post.author?.username}</span>
                                </div>
                                <span className="post-date">
                                    {new Date(post.createdAt).toLocaleDateString()}
                                </span>
                            </div>
                            <Link to={`/post/${post._id}`} className="read-more-btn">
                                Read More
                            </Link>
                        </div>
                    </article>
                ))}
            </div>
        </div>
    );
};

export default BlogList;
