import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../utils/axios';
import './CreatePost.css';

const CreatePost = () => {
    const navigate = useNavigate();
    const fileInputRef = useRef(null);

    // Form state
    const [formData, setFormData] = useState({
        title: '',
        content: '',
        category: 'general',
        tags: []
    });

    // UI state
    const [touched, setTouched] = useState({
        title: false,
        content: false
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [image, setImage] = useState(null);
    const [imagePreview, setImagePreview] = useState(null);
    const [tagInput, setTagInput] = useState('');

    // Categories with emojis
    const categories = [
        { value: 'general', label: 'ðŸ“„ General' },
        { value: 'technology', label: 'ðŸ’» Technology' },
        { value: 'lifestyle', label: 'ðŸŒŸ Lifestyle' },
        { value: 'travel', label: 'âœˆï¸ Travel' },
        { value: 'food', label: 'ðŸ½ï¸ Food' },
        { value: 'other', label: 'ðŸ“Œ Other' }
    ];

    // Form validation
    const validateField = (name, value) => {
        switch (name) {
            case 'title':
                return !value.trim() ? 'Title is required' : '';
            case 'content':
                return !value.trim() ? 'Content is required' : '';
            default:
                return '';
        }
    };

    // Event handlers
    const handleBlur = (field) => {
        setTouched(prev => ({
            ...prev,
            [field]: true
        }));
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));

        if (touched[name]) {
            const error = validateField(name, value);
            setError(error);
        }
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            if (file.size > 5 * 1024 * 1024) {
                setError('Image must be less than 5MB');
                return;
            }

            if (!file.type.match('image.*')) {
                setError('Please upload an image file');
                return;
            }

            setImage(file);
            setImagePreview(URL.createObjectURL(file));
            setError('');
        }
    };

    const handleTagInput = (e) => {
        if (e.key === 'Enter' && tagInput.trim()) {
            e.preventDefault();
            if (!formData.tags.includes(tagInput.trim())) {
                setFormData(prev => ({
                    ...prev,
                    tags: [...prev.tags, tagInput.trim()]
                }));
            }
            setTagInput('');
        }
    };

    const removeTag = (tagToRemove) => {
        setFormData(prev => ({
            ...prev,
            tags: prev.tags.filter(tag => tag !== tagToRemove)
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Mark all fields as touched
        setTouched({
            title: true,
            content: true
        });

        // Validate all fields
        const titleError = validateField('title', formData.title);
        if (titleError) {
            setError(titleError);
            return;
        }

        const contentError = validateField('content', formData.content);
        if (contentError) {
            setError(contentError);
            return;
        }

        setLoading(true);
        setError('');

        try {
            // Check authentication
            const token = localStorage.getItem('token');
            if (!token) {
                throw new Error('Please login to create a post');
            }

            // Prepare form data
            const formDataToSend = new FormData();
            formDataToSend.append('title', formData.title.trim());
            formDataToSend.append('content', formData.content.trim());
            formDataToSend.append('category', formData.category);
            
            if (formData.tags.length > 0) {
                formDataToSend.append('tags', JSON.stringify(formData.tags));
            }
            
            if (image) {
                formDataToSend.append('image', image);
            }

            // Send request
            const response = await api.post('/blog', formDataToSend, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'multipart/form-data'
                }
            });

            if (response.data.success) {
                navigate(`/post/${response.data.post._id}`);
            } else {
                throw new Error(response.data.message || 'Failed to create post');
            }
        } catch (err) {
            console.error('Create post error:', err);
            if (err.response?.status === 401) {
                navigate('/login', { state: { from: '/create-post' } });
            }
            setError(err.response?.data?.message || err.message || 'Failed to create post');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="create-post-container">
            <div className="create-post-card">
                <h1>Create New Post</h1>
                {error && <div className="error-message">{error}</div>}
                
                <form onSubmit={handleSubmit} noValidate>
                    {/* Title Input */}
                    <div className="form-group">
                        <label htmlFor="title">
                            Title <span className="required">*</span>
                        </label>
                        <input
                            type="text"
                            id="title"
                            name="title"
                            value={formData.title}
                            onChange={handleChange}
                            onBlur={() => handleBlur('title')}
                            className={`form-input ${touched.title && !formData.title.trim() ? 'invalid' : ''}`}
                            placeholder="Enter post title"
                        />
                        {touched.title && !formData.title.trim() && (
                            <div className="field-error">Title is required</div>
                        )}
                    </div>

                    {/* Category Select */}
                    <div className="form-group">
                        <label htmlFor="category">Category</label>
                        <select
                            id="category"
                            name="category"
                            value={formData.category}
                            onChange={handleChange}
                            className="form-select"
                        >
                            {categories.map(cat => (
                                <option key={cat.value} value={cat.value}>
                                    {cat.label}
                                </option>
                            ))}
                        </select>
                    </div>

                    {/* Tags Input */}
                    <div className="form-group">
                        <label>Tags</label>
                        <div className="tags-input-container">
                            <div className="tags-list">
                                {formData.tags.map((tag, index) => (
                                    <span key={index} className="tag">
                                        {tag}
                                        <button
                                            type="button"
                                            onClick={() => removeTag(tag)}
                                            className="remove-tag"
                                        >
                                            Ã—
                                        </button>
                                    </span>
                                ))}
                            </div>
                            <input
                                type="text"
                                value={tagInput}
                                onChange={(e) => setTagInput(e.target.value)}
                                onKeyPress={handleTagInput}
                                placeholder="Add tags (press Enter)"
                                className="tag-input"
                            />
                        </div>
                    </div>

                    {/* Image Upload */}
                    <div className="form-group">
                        <label>Cover Image</label>
                        <div 
                            className="image-upload-area"
                            onClick={() => fileInputRef.current.click()}
                        >
                            {imagePreview ? (
                                <div className="image-preview">
                                    <img src={imagePreview} alt="Preview" />
                                    <button
                                        type="button"
                                        className="remove-image"
                                        onClick={(e) => {
                                            e.stopPropagation();
                                            setImage(null);
                                            setImagePreview(null);
                                        }}
                                    >
                                        Ã—
                                    </button>
                                </div>
                            ) : (
                                <div className="upload-placeholder">
                                    <i className="fas fa-cloud-upload-alt"></i>
                                    <p>Click to upload image</p>
                                    <span>Max size: 5MB</span>
                                </div>
                            )}
                            <input
                                type="file"
                                ref={fileInputRef}
                                onChange={handleImageChange}
                                accept="image/*"
                                style={{ display: 'none' }}
                            />
                        </div>
                    </div>

                    {/* Content Textarea */}
                    <div className="form-group">
                        <label htmlFor="content">
                            Content <span className="required">*</span>
                        </label>
                        <textarea
                            id="content"
                            name="content"
                            value={formData.content}
                            onChange={handleChange}
                            onBlur={() => handleBlur('content')}
                            className={`form-textarea ${touched.content && !formData.content.trim() ? 'invalid' : ''}`}
                            placeholder="Write your post content here..."
                            rows="12"
                        />
                        {touched.content && !formData.content.trim() && (
                            <div className="field-error">Content is required</div>
                        )}
                    </div>

                    {/* Form Actions */}
                    <div className="form-actions">
                        <button 
                            type="button" 
                            className="cancel-button"
                            onClick={() => navigate(-1)}
                        >
                            Cancel
                        </button>
                        <button 
                            type="submit" 
                            className="submit-button" 
                            disabled={loading}
                        >
                            {loading ? (
                                <>
                                    <span className="loading-spinner"></span>
                                    Creating Post...
                                </>
                            ) : (
                                'Create Post'
                            )}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default CreatePost;
