import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../../utils/axios';
import './CreatePost.css';

const CreatePost = () => {
    const navigate = useNavigate();
    const fileInputRef = useRef(null);
    
    // State aligned with Post model
    const [formData, setFormData] = useState({
        title: '',
        content: '',
        category: 'general',
        tags: [],
        status: 'published'
    });

    const [image, setImage] = useState(null);
    const [imagePreview, setImagePreview] = useState(null);
    const [validationErrors, setValidationErrors] = useState({});
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    // Categories from Post model enum
    const categories = [
        { value: 'general', label: 'ðŸ“„ General' },
        { value: 'technology', label: 'ðŸ’» Technology' },
        { value: 'lifestyle', label: 'ðŸŒŸ Lifestyle' },
        { value: 'travel', label: 'âœˆï¸ Travel' },
        { value: 'food', label: 'ðŸ½ï¸ Food' },
        { value: 'other', label: 'ðŸ“Œ Other' }
    ];

    const validateForm = () => {
        const errors = {};
        
        // Title validation
        if (!formData.title.trim()) {
            errors.title = 'Title is required';
        }

        // Content validation
        if (!formData.content.trim()) {
            errors.content = 'Content is required';
        }

        setValidationErrors(errors);
        return Object.keys(errors).length === 0;
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            // Validate file size (5MB limit)
            if (file.size > 5 * 1024 * 1024) {
                setError('Image size should be less than 5MB');
                return;
            }

            // Validate file type
            if (!['image/jpeg', 'image/png', 'image/gif'].includes(file.type)) {
                setError('Please upload a valid image file (JPG, PNG, or GIF)');
                return;
            }

            setImage(file);
            setImagePreview(URL.createObjectURL(file));
            setError('');
        }
    };

    const handleDragOver = (e) => {
        e.preventDefault();
        e.stopPropagation();
    };

    const handleDrop = (e) => {
        e.preventDefault();
        e.stopPropagation();
        const file = e.dataTransfer.files[0];
        if (file) {
            handleImageFile(file);
        }
    };

    const handleImageFile = (file) => {
        if (!['image/jpeg', 'image/png', 'image/gif'].includes(file.type)) {
            setError('Please upload a valid image file (JPG, PNG, or GIF)');
            return;
        }
        if (file.size > 5 * 1024 * 1024) {
            setError('Image size should be less than 5MB');
            return;
        }
        setImage(file);
        setImagePreview(URL.createObjectURL(file));
        setError('');
    };

    const removeImage = () => {
        setImage(null);
        setImagePreview(null);
        if (fileInputRef.current) {
            fileInputRef.current.value = '';
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!validateForm()) {
            return;
        }

        setLoading(true);
        setError('');

        try {
            const token = localStorage.getItem('token');
            if (!token) {
                throw new Error('Please login to create a post');
            }

            const formDataToSend = new FormData();
            formDataToSend.append('title', formData.title.trim());
            formDataToSend.append('content', formData.content.trim());
            formDataToSend.append('category', formData.category);
            
            if (formData.tags.length > 0) {
                formDataToSend.append('tags', JSON.stringify(formData.tags));
            }
            
            if (image) {
                formDataToSend.append('image', image);
            }

            // Log form data for debugging
            console.log('Submitting post with data:', {
                title: formData.title,
                category: formData.category,
                hasImage: !!image,
                tags: formData.tags
            });

            const response = await api.post('/blog', formDataToSend, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'multipart/form-data'
                }
            });

            console.log('Server response:', response.data);

            if (response.data.success) {
                navigate(`/post/${response.data.post._id}`);
            } else {
                throw new Error(response.data.message || 'Failed to create post');
            }
        } catch (err) {
            console.error('Create post error:', err);
            setError(err.response?.data?.message || err.message || 'Failed to create post');
        } finally {
            setLoading(false);
        }
    };

    const handleTagInput = (e) => {
        if (e.key === 'Enter' && e.target.value.trim()) {
            e.preventDefault();
            const newTag = e.target.value.trim().toLowerCase();
            if (!formData.tags.includes(newTag)) {
                setFormData({
                    ...formData,
                    tags: [...formData.tags, newTag]
                });
            }
            e.target.value = '';
        }
    };

    const removeTag = (tagToRemove) => {
        setFormData({
            ...formData,
            tags: formData.tags.filter(tag => tag !== tagToRemove)
        });
    };

    return (
        <div className="create-post-container">
            <div className="create-post-card">
                <h1>Create New Post</h1>
                {error && <div className="error-message">{error}</div>}
                
                <form onSubmit={handleSubmit} noValidate>
                    <div className="form-group">
                        <label htmlFor="title">
                            Title <span className="required">*</span>
                        </label>
                        <input
                            type="text"
                            id="title"
                            value={formData.title}
                            onChange={(e) => setFormData({...formData, title: e.target.value})}
                            className={`form-input ${validationErrors.title ? 'invalid' : ''}`}
                            placeholder="Enter post title"
                        />
                        {validationErrors.title && (
                            <div className="error-text">{validationErrors.title}</div>
                        )}
                    </div>

                    <div className="form-group">
                        <label htmlFor="category">Category</label>
                        <select
                            id="category"
                            value={formData.category}
                            onChange={(e) => setFormData({...formData, category: e.target.value})}
                            className="form-select"
                        >
                            {categories.map(cat => (
                                <option key={cat.value} value={cat.value}>
                                    {cat.label}
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className="form-group">
                        <label>Tags</label>
                        <div className="tags-container">
                            {formData.tags.map((tag, index) => (
                                <span key={index} className="tag">
                                    {tag}
                                    <button 
                                        type="button" 
                                        onClick={() => removeTag(tag)}
                                        className="remove-tag"
                                    >
                                        Ã—
                                    </button>
                                </span>
                            ))}
                            <input
                                type="text"
                                placeholder="Add tags (press Enter)"
                                onKeyPress={handleTagInput}
                                className="tag-input"
                            />
                        </div>
                    </div>

                    <div className="form-group">
                        <label>Cover Image</label>
                        <div 
                            className="image-upload-area"
                            onDragOver={handleDragOver}
                            onDrop={handleDrop}
                            onClick={() => fileInputRef.current.click()}
                        >
                            {!imagePreview ? (
                                <div className="upload-placeholder">
                                    <i className="fas fa-cloud-upload-alt"></i>
                                    <p>Click or drag image to upload</p>
                                    <span>Max size: 5MB (JPG, PNG, GIF)</span>
                                </div>
                            ) : (
                                <div className="image-preview">
                                    <img src={imagePreview} alt="Preview" />
                                    <button 
                                        type="button"
                                        className="remove-image"
                                        onClick={(e) => {
                                            e.stopPropagation();
                                            removeImage();
                                        }}
                                    >
                                        Ã—
                                    </button>
                                </div>
                            )}
                            <input
                                type="file"
                                ref={fileInputRef}
                                onChange={handleImageChange}
                                accept="image/*"
                                style={{ display: 'none' }}
                            />
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="content">
                            Content <span className="required">*</span>
                        </label>
                        <textarea
                            id="content"
                            value={formData.content}
                            onChange={(e) => setFormData({...formData, content: e.target.value})}
                            className={`form-textarea ${validationErrors.content ? 'invalid' : ''}`}
                            placeholder="Write your post content here..."
                            rows="12"
                        />
                        {validationErrors.content && (
                            <div className="error-text">{validationErrors.content}</div>
                        )}
                    </div>

                    <div className="form-actions">
                        <button 
                            type="button" 
                            className="cancel-button"
                            onClick={() => navigate(-1)}
                        >
                            Cancel
                        </button>
                        <button 
                            type="submit" 
                            className="submit-button" 
                            disabled={loading}
                        >
                            {loading ? (
                                <>
                                    <span className="loading-spinner"></span>
                                    Creating Post...
                                </>
                            ) : (
                                'Create Post'
                            )}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default CreatePost;
