import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import CategoryFilter from './CategoryFilter';
import './BlogList.css';

const BlogList = () => {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedCategory, setSelectedCategory] = useState(null);

    const categories = ['Technology', 'Travel', 'Food', 'Lifestyle', 'Other'];

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/blog');
                setPosts(response.data);
            } catch (err) {
                setError('Failed to fetch posts');
            } finally {
                setLoading(false);
            }
        };

        fetchPosts();
    }, []);

    const filteredPosts = selectedCategory
        ? posts.filter(post => post.category === selectedCategory)
        : posts;

    if (loading) return <div className="loading">Loading...</div>;
    if (error) return <div className="error-message">{error}</div>;

    return (
        <div className="blog-list-container">
            <CategoryFilter
                categories={categories}
                selectedCategory={selectedCategory}
                onCategoryChange={setSelectedCategory}
            />

            <div className="blog-list">
                {filteredPosts.map(post => (
                    <div key={post._id} className="blog-card">
                        <div className="post-image">
                            <img 
                                src={post.imageUrl || '/images/default-blog.jpg'} 
                                alt={post.title}
                                onError={(e) => {
                                    e.target.src = '/images/default-blog.jpg';
                                }}
                            />
                            <span className="category-tag">{post.category}</span>
                        </div>
                        <div className="post-content">
                            <h2>{post.title}</h2>
                            <p>{post.content.substring(0, 150)}...</p>
                            <div className="post-meta">
                                <div className="author-info">
                                    <img 
                                        src={post.author?.avatar || '/images/default-avatar.jpg'} 
                                        alt={post.author?.username}
                                        className="author-avatar"
                                    />
                                    <span>{post.author?.username}</span>
                                </div>
                                <span className="post-date">
                                    {new Date(post.createdAt).toLocaleDateString()}
                                </span>
                            </div>
                            <Link to={`/post/${post._id}`} className="read-more-btn">
                                Read More
                            </Link>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default BlogList;
