import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../../utils/axios';
import './BlogPost.css';

const BlogPost = () => {
    const [post, setPost] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [deleteLoading, setDeleteLoading] = useState(false);
    const { id } = useParams();
    const navigate = useNavigate();
    const currentUser = JSON.parse(localStorage.getItem('user'));

    // Add this function to handle image URLs
    const getImageUrl = (imageUrl) => {
        if (!imageUrl) return '/images/default-blog.jpg';
        if (imageUrl.startsWith('http')) return imageUrl;
        return `${process.env.REACT_APP_API_URL}${imageUrl}`;
    };

    useEffect(() => {
        fetchPost();
    }, [id]);

    const fetchPost = async () => {
        try {
            const response = await api.get(`/blog/${id}`);
            if (response.data.success) {
                setPost(response.data.post);
            } else {
                setError('Failed to fetch post');
            }
            setLoading(false);
        } catch (err) {
            setError(err.response?.data?.message || 'Failed to fetch post');
            setLoading(false);
        }
    };

    const handleDelete = async () => {
        // Clear any previous errors
        setError('');

        // Check for authentication
        if (!currentUser) {
            setError('Please login to delete this post');
            return;
        }

        // Check for authorization
        if (post.author._id !== currentUser._id) {
            setError('You are not authorized to delete this post');
            return;
        }

        try {
            setDeleteLoading(true);
            const response = await api.delete(`/blog/${id}`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`
                }
            });

            if (response.data.success) {
                navigate('/');
            } else {
                setError(response.data.message || 'Failed to delete post');
                setDeleteLoading(false);
            }
        } catch (err) {
            console.error('Delete error:', err);
            setError(err.response?.data?.message || 'Failed to delete post');
            setDeleteLoading(false);
        }
    };

    const confirmDelete = () => {
        const isConfirmed = window.confirm(
            'Are you sure you want to delete this post? This action cannot be undone.'
        );
        if (isConfirmed) {
            handleDelete();
        }
    };

    if (loading) return <div className="loading">Loading post...</div>;
    if (error) return <div className="error-message">{error}</div>;
    if (!post) return <div className="error-message">Post not found</div>;

    return (
        <div className="blog-post">
            <div className="post-header">
                <h1>{post.title}</h1>
                {currentUser && post.author && currentUser._id === post.author._id && (
                    <div className="post-actions">
                        <button 
                            onClick={() => navigate(`/edit-post/${id}`)} 
                            className="edit-button"
                            disabled={deleteLoading}
                        >
                            <i className="fas fa-edit"></i> Edit
                        </button>
                        <button 
                            onClick={confirmDelete} 
                            className="delete-button"
                            disabled={deleteLoading}
                        >
                            {deleteLoading ? (
                                <span className="loading-spinner"></span>
                            ) : (
                                <><i className="fas fa-trash"></i> Delete</>
                            )}
                        </button>
                    </div>
                )}
            </div>

            {error && <div className="error-message">{error}</div>}

            <div className="post-image">
                <img 
                    src={getImageUrl(post.imageUrl)} 
                    alt={post.title}
                    onError={(e) => {
                        console.log('Image failed to load:', post.imageUrl);
                        e.target.src = '/images/default-blog.jpg';
                        e.target.onerror = null; // Prevents infinite loop
                    }}
                />
            </div>

            <div className="post-meta">
                <div className="meta-left">
                    <span className="category">{post.category}</span>
                    <span className="date">
                        {new Date(post.createdAt).toLocaleDateString()}
                    </span>
                </div>
                {post.author && (
                    <div className="author-info">
                        <img 
                            src={post.author.avatar || '/images/default-avatar.jpg'}
                            alt={post.author.username}
                            className="author-avatar"
                            onError={(e) => {
                                e.target.src = '/images/default-avatar.jpg';
                                e.target.onerror = null;
                            }}
                        />
                        <span className="author-name">By {post.author.username}</span>
                    </div>
                )}
            </div>

            <div className="post-content">{post.content}</div>

            {post.tags && post.tags.length > 0 && (
                <div className="post-tags">
                    {post.tags.map((tag, index) => (
                        <span key={index} className="tag">#{tag}</span>
                    ))}
                </div>
            )}
        </div>
    );
};

export default BlogPost;
